import React, { Component } from 'react';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import AddIcon from '@material-ui/icons/Add';
import Typography from '@material-ui/core/Typography';
import axios from 'axios';

const styles = ((theme) => ({
  Card: {
    width: '30%',
    height: 100,
    marginTop: '2%',
    marginLeft: '35%',
    border: '1px solid black',
  },
  root: {
    marginTop: '8%'
  },
  AddIcon: {
    marginRight: '80%',
    marginTop: '3%',
    width: '10%',
    height: '65%'
  },
  Typography: {
    marginTop: '-11%',
    fontSize: '30px'
  }
}));

class MainMentorPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      mentor_name: '',
      array: []
    }
  }

  componentDidMount() {
    axios
      .get("http://localhost:8000/mentor_mentee/mentors")
      .then(response => {
        this.setState({ array: response, })
        console.log(response, "22222222222222222")
      });
  }

  render() {
    const { classes, } = this.props;
    // const { array } = this.state.array;
    return (
      <div>
        {this.state.array.map(item, i =>{
           <Card className={classes.Card} >
           <AddIcon className={classes.AddIcon}>{item.mentor_name}</AddIcon>
           <Typography className={classes.Typography} > Add New Mentor </Typography>
         </Card>


        })}
      </div>
    );
  }
}

export default withStyles(styles)(MainMentorPage);
