import React, { Component } from 'react';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
import AppBar from 'material-ui/AppBar';
import RaisedButton from 'material-ui/RaisedButton';
import TextField from 'material-ui/TextField';
import Card from '@material-ui/core/Card';
import axios from 'a'

const validEmailRegex = RegExp(
  /^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i
);

const validateForm = errors => {
  let valid = true;
  Object.values(errors).forEach(val => val.length > 0 && (valid = false));
  return valid;
};

class Signup_Page extends Component {
  constructor(props) {
    super(props);
    this.state = {
      first_name: '',
      last_name: '',
      UserName: '',
      email: '',
      password: '',
      errors: {
        first_name: '',
        last_name: '',
        UserName: '',
        email: '',
        password: '',
      }
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  onSubmit = e => {
    e.preventDefault();
    const getData = axios
      .get(
        "http://localhost:8000/login",
        {
          params: {
            email: this.state.email,
            password: this.state.password
          }
        },
        { headers: { "Content-Type": "application/json" } }
      )
      .then(res => {
        console.log(res);
        localStorage.setItem("token", res.data);
        const token = localStorage.getItem("token");
        // console.log(localStorage.getItem("token"));

        const n = axios
          .get("http://localhost:8000/verify", { params: { token: token } })
          .then(respo => {
            if (respo.data === true) {
              window.location.href = "/home";
            } else {
              window.location.href = "/";
            }
          });

        this.setState({
          loading: false
        });
        console.log(n);
      })
      .catch(err => {
        console.log(err);
        this.setState({
          loading: false
        });
      });
    console.log(getData);
  };


  handleChange = (event) => {
    event.preventDefault();
    const { name, value } = event.target;
    let errors = this.state.errors;

    switch (name) {
      case 'UserName':
        errors.fullName =
          value.length < 5
            ? 'Full Name must be at least 5 characters long!'
            : '';
        break;
      case 'email':
        errors.email =
          validEmailRegex.test(value)
            ? ''
            : 'Email is not valid!';
        break;
      case 'password':
        errors.password =
          value.length < 8
            ? 'Password must be at least 8 characters long!'
            : '';
        break;
      default:
        break;
    }

    this.setState({ errors, [name]: value });
  }


  handleSubmit = (event) => {
    event.preventDefault();
    if (validateForm(this.state.errors)) {
      console.info('Valid Form')
    } else {
      console.error('Invalid Form')
    }

    const data = new FormData();
    data.append('file', this.uploadInput.files[0]);
    data.append('filename', this.fileName.value);

    fetch('http://localhost:8000/mentor_mentee/signup', {
      method: 'POST',
      body: data
    }).then(response => {
      response.json().then(body => {
        this.setState({ imageURL: `http://localhost:8000/${body.file}` });
      });
    });

  }
  render() {
    return (
      <div>
        <MuiThemeProvider>
          <div>
            <AppBar title="SignUp Page" />
            <h1>Create Account</h1>
            <Card style={{ width: '30%', height: '50%', marginLeft: '40%', marginTop: '5%', border: '1px solid black' }}>
              <TextField hintText="Enter your First Name" floatingLabelText="First Name" />
              <br />
              <TextField hintText="Enter your Last Name" floatingLabelText="Last Name" />
              <br />
              <TextField hintText="Enter your UserName" floatingLabelText="UserName" />
              <br />
              <TextField hintText="Enter your Email" type="email" floatingLabelText="Email" />
              <br />
              <TextField type="password" hintText="Enter your Password" floatingLabelText="Password" />
              <br />
              <RaisedButton label="Signup" primary={true} style={style} />
            </Card>
          </div>
        </MuiThemeProvider>
      </div>
    );
  }
}
const style = {
  margin: 15,
};
export default Signup_Page;
