import React from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Button from '@material-ui/core/Button';


const validEmailRegex = RegExp(
  /^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i
);
const validateForm = errors => {
  let valid = true;
  Object.values(errors).forEach(val => val.length > 0 && (valid = false));
  return valid;
};

export default class Signup extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      fistName: " ",
      LastName: " ",
      UserName: " ",
      email: " ",
      password: " ",

    };
  }

  handleChange = (event) => {
    event.preventDefault();
    const { name, value } = event.target;


  }
  Submit = (event) => {
    event.preventDefault();

    const newUser = {
      fistName: this.state.fistName,
      LastName: this.state.LastName,
      UserName: this.state.UserName,
      email: this.state.email,
      password: this.state.password,

    };

    axios.post(
      "http://localhost:8000/signup",newUser)
      .then((res) =>{
        console.log(res.data)
      }).cath


  }




  render() {
    const { fistName, LastName, UserName, email, password } = this.state;
    console.log(this.state)

    return (
      <div className='wrapper'>
        <div className='form-wrapper'>
          <h2>Create Account</h2>
          <form onSubmit={this.handleSubmit} noValidate>
            <div className='firstName'>
              <label htmlFor="FirstName"> First Name</label>
              <input type='text' name='FirstName' onChange={this.handleChange} noValidate />

              {/* <span className='error'>{errors.fullName}</span> */}
            </div>
            <div className='lastName'>
              <label htmlFor="LastName"> Last Name</label>
              <input type='text' name='LastName' onChange={this.handleChange} noValidate />

              {/* <span className='error'>{errors.fullName}</span> */}
            </div>
            <div className='email'>
              <label htmlFor="email">Email</label>
              <input type='email' name='email' onChange={this.handleChange} noValidate />

              {/* <span className='error'>{errors.email}</span> */}
            </div>
            <div className='password'>
              <label htmlFor="password">Password</label>
              <input type='password' name='password' onChange={this.handleChange} noValidate />

              {/* <span className='error'>{errors.password}</span> */}
            </div>

            <Button
              // disabled={!isEnabled}
              variant="contained"
              color="primary"
              onClick={this.Submit}
            >
              SUBMIT
             </Button>

          </form>
        </div>
      </div>
    );
  }
}
