import React, { Component } from 'react';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import AddIcon from '@material-ui/icons/Add';
import Typography from '@material-ui/core/Typography';
import axios from 'axios';

const styles = ((theme) => ({
  Card: {
    width: '30%',
    height: 100,
    marginTop: '2%',
    marginLeft: '35%',
    border: '1px solid black',
  },
  root: {
    marginTop: '8%'
  },
  AddIcon: {
    marginRight: '80%',
    marginTop: '3%',
    width: '10%',
    height: '65%'
  },
  Typography: {
    marginTop: '-11%',
    fontSize: '30px'
  }
}));

class MainMentorPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      mentor_name: '',
    }
  }

  componentDidMount() {
    axios
      .get("http://localhost:8000/mentor_mentee/mentors")
      .then(response => {
        this.setState({ array: response.data, })
        console.log(response.data, "22222222222222222")
      });
  }

  render() {
    // const { classes, } = this.props;
    const { array } = this.state;
    console.log(array, "RRRRRRRRRRRRRRRRRrrrrr")
    return (
      <div>
        {array.map((array, i) => (
          <div>
            <Card>
              {response.mentor_name}
            </Card>
          </div>
        ))}
      </div>
    );
  }
}

export default withStyles(styles)(MainMentorPage);
