import React, { Component } from 'react';
import { Card, TextField, withStyles, CardContent, Button } from "@material-ui/core";
import EditIcon from '@material-ui/icons/Edit';
import axios from 'axios';

const styles = theme => ({
    card: {
        height: 700,
        width: '50%',
        marginTop: '5%',
        paddingTop: '2%',
        marginLeft: '25%',
        border: '1px solid black'
    },
    CardContent: {
        marginTop: '-10%',
    },
    TextField: {
        width: '70%',
        marginTop: '3%'
    },
    textField: {
        width: '50%',
        marginTop: '-14%'
    },
    Button: {
        marginTop: '6%',
        background: 'black',
        color: 'white',
        width: '10%',
        marginRight: '30%',
        marginLeft: '30%',
    },
    button: {
        marginLeft: '5%',
        marginTop: '1%',
        background: 'black',
        color: 'white',
        width: '10%',
        marginTop: '-25%'
    },
    EditIcon: {
        marginLeft: '95%',
        width: '5%',
        height: '40%',
        marginTop: '-15%'
    }

});

const handleChange = (event) => {
    this.setState({ username: event.target.value })
}

const handleSubmit = (event) => {
    event.preventDefault();
    const newUser = {
        Mentor_Name: this.state.Mentor_Name,
        Mentee_Name: this.state.Mentee_Name,
        Mentoring_Diversion: this.state.Mentoring_Diversion,
        Mentee_Feedback: this.state.Mentee_Feedback,
        Which_thing_she_has_to_improve: this.state.Which_thing_she_has_to_improve,
        English_Feedback: this.state.English_Feedback,
    };

    axios.post(
        "http://localhost:8000/mentor_mentee/mentor_info", newUser({
            params: {
                Mentor_Name: this.state.Mentor_Name,
                Mentee_Name: this.state.Mentee_Name,
                Mentoring_Diversion: this.state.Mentoring_Diversion,
                Mentee_Feedback: this.state.Mentee_Feedback,
                Which_thing_she_has_to_improve: this.state.Which_thing_she_has_to_improve,
                English_Feedback: this.state.English_Feedback,
            }
        })
    ).catch((error) => {
        console.log(error)
    });
}


class Mentor extends Component {
    render() {
        const { classes, } = this.props;
        return (
            <Card className={classes.card} onSubmit={this.handleSubmit}>
                <EditIcon className={classes.EditIcon}></EditIcon>
                <TextField className={classes.textField} label="Mentor Name" /><Button className={classes.button}>Search</Button>
                <CardContent className={classes.CardContent}>
                    <TextField className={classes.TextField} value={this.state.password} label="Mentor_Name" />
                    <br />
                    <TextField className={classes.TextField} value={this.state.password} label="Mentee_Name" />
                    <br />
                    <TextField className={classes.TextField}value={this.state.password} label="Mentoring_Diversion" />
                    <br />
                    <TextField className={classes.TextField} label="Mentee_Feedback" />
                    <br />
                    <TextField className={classes.TextField} label="Which_thing_she/he_has_to_improve" />
                    <br />
                    <TextField className={classes.TextField} label="English_Feedback" />
                    <Button className={classes.Button} onChange={this.handleChange}>Submit</Button>
                </CardContent>
            </Card>
        )
    }
}

export default withStyles(styles)(Mentor);
