import React, { Component } from "react";
import axios from "axios";
import { Navbar, Button } from "react-bootstrap";
import { Link } from "react-router-dom";

var validEmailRe = RegExp(
  /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:s@\"]{2,})$/i
);

class Register extends Component {
  constructor() {
    super();
    this.state = {
      Username: "",
      email: "",
      password: "",
      errors: {
        Username: "",
        email: "",
        password: ""
      }
    };

    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }
  validateUsername = userName => {
    return userName && userName.length >= 5;
  };
  onChange = e => {
    const { name, value } = e.target;
    let errors = this.state.errors;
    const newErros = { ...errors };
    switch (name) {
      case "Username":
        newErros.Username = this.validateUsername(value)
          ? ""
          : "Full Name must be 5 characters long!";
        break;
      case "email":
        newErros.email = validEmailRe.test(value) ? "" : "Email is not valid!";
        break;
      case "password":
        newErros.password =
          value.length < 8 ? "Password must be 8 characters long!" : "";
        break;
      default:
        break;
    }
    this.setState({
      errors: newErros,
      [name]: value
    });
  };
  goToHome = () => {
    this.props.history.replace("/login");
  };
  onSubmit = async e => {
    e.preventDefault();
    const newuser = {
      Username: this.state.Username,
      email: this.state.email,
      password: this.state.password
    };

    const response = await axios.post(
      "http://localhost:8000/register",
      newuser
    );
    if (response.errors === null) {
      console.log("komal");
    } else {
      this.goToHome();
    }
  };

  render() {
    const { email, Username, password } = this.state;
    console.log(this.state);

    const isEnabled =
      validEmailRe.test(email) && password.length > 8 && Username.length > 4;

    return (
      <div className='wrapper'>
      <div className='form-wrapper'>
        <h2>Create Account</h2>
        <form onSubmit={this.handleSubmit} noValidate>
          <div className='firstName'>
            <label htmlFor="FirstName"> First Name</label>
            <input type='text' name='FirstName' onChange={this.handleChange} noValidate />
          </div>
          <div className='lastName'>
            <label htmlFor="LastName"> Last Name</label>
            <input type='text' name='LastName' onChange={this.handleChange} noValidate />
          </div>
          <div className='email'>
            <label htmlFor="email">Email</label>
            <input type='email' name='email' onChange={this.handleChange} noValidate />
          </div>
          <div className='password'>
            <label htmlFor="password">Password</label>
            <input type='password' name='password' onChange={this.handleChange} noValidate />
          </div>

          <Button variant="contained" color="primary" onClick={this.Submit} > Signup </Button>
          <Button variant="contained" style={{marginLeft: '40%'}} > Login </Button>
        </form>
      </div>
    </div>
    );
  }
}

export default Register;
