import React, { Component } from "react";
import PropTypes from "prop-types";
import { Card, CardContent, CardActions, Typography, CardMedia, withStyles } from "@material-ui/core";

const styles = theme => ({
    card: {
        height: 700,
        width: '50%',
        background: 'black',
        marginTop: '7%',
        marginLeft: '25%'
    },
});

class CardComponent extends Component {
    render() {
        const { classes, imageUrl, title, caption, text, actionLink } = this.props;
        return (
            <Card className={classes.card}>
                <CardMedia image={imageUrl} title="Flower" className={classes.media} />
                <CardContent className={classes.content}>
                    <div className={classes.cardText}>
                        <Typography
                            variant="caption"
                            className={classes.caption}
                            gutterBottom
                        >
                            {caption}
                        </Typography>
                        <Typography variant="h6" className={classes.title} gutterBottom>
                            {title}
                        </Typography>
                        <Typography variant="body1">{text}</Typography>
                    </div>

                </CardContent>
            </Card>
        );
    }
}

CardComponent.propTypes = {
    classes: PropTypes.object.isRequired
};

export default withStyles(styles)(CardComponent);
